		$hasil=$this->db->query(" select count(*) as jumlah FROM $tabel where $field='$nomor' and kd_skpd = '$kd_skpd' ");
		} else{
		$hasil=$this->db->query(" select count(*) as jumlah FROM (select $field as nomor FROM $tabel UNION 
		SELECT $field2 as nomor FROM $tabel2)a WHERE a.nomor = '$nomor' ");		
		}
		foreach ($hasil->result_array() as $row){
		$jumlah=$row['jumlah'];	
		}
		if($jumlah>0){
